async function callApi(msg){

    try {
        node.status({fill:"blue",shape:"ring",text:"requesting"});
       let data = await axios({
            url: msg.url,
            data: msg.payload,
            headers: msg.headers,
            method: msg.method,
            // responseType: msg.responseType
        })
        node.send({...msg,responseType:"json",payload:data?.data})


        node.status({fill:"green",shape:"ring",text:"completed"});
    } catch (error) {

        node.send({...msg,payload:error})

        // node.done()
        node.status({fill:"red",shape:"ring",text:"failed"});
        node.error("Axios error",{error:error.response?.data,...msg});
    };
}
