const pdf = global.get('pdf')
const fs = global.get('fs');
const { PDFDocument } = pdf

async function splitFile() {
    try {
        const pdfBytes = fs.readFileSync('/Users/shravanpm/Downloads/traffic_pub_gen19.pdf');
        const pdfDoc = await PDFDocument.load(pdfBytes);

        const maxFileSizeInBytes = 1024 * 1024; // 1mb
        const pageCount = pdfDoc.getPageCount();
        let currentPageIndex = 0;
        let currentFileSize = 0;
        let count = 0
        while (currentPageIndex < pageCount) {
            let endIndex = currentPageIndex;
            let newPdfDoc = await PDFDocument.create();

            while (endIndex < pageCount) {
                const [copiedPage] = await newPdfDoc?.copyPages(pdfDoc, [endIndex]);
                // const [firstDonorPage] = await pdfDoc.copyPages(firstDonorPdfDoc, [0])
                newPdfDoc.addPage(copiedPage);
                console.log({ endIndex })
                const pdfBytes = await newPdfDoc.save();
                currentFileSize = pdfBytes.length;

                endIndex++;

                if (currentFileSize > maxFileSizeInBytes) {
                    break;
                }


            }
            count = count + 1
            console.log({ count })

            const newPdfBytes = await newPdfDoc.save();

            fs.writeFileSync(`/Users/shravanpm/Desktop/log/page${count}.pdf`, newPdfBytes);
            currentPageIndex = endIndex;
        }
    } catch (error) {
        console.log("error",error)
    };
}

