                let businessID = outstandingFile["New account"] ? msg.erpIdAndBusinessIdMapping[`${outstandingFile["New account"]}${outstandingFile["Account"]}`] : msg.erpIdAndBusinessIdMapping[`${outstandingFile["Account"]}`]
