                    documentNo: outstandingFile["DocumentNo"].toString() ?? "",
                    amount: outstandingFile["   Amt in loc.cur."],
                    dueDate: actualDays,
                    documentDate: new Date(invoiceDate)?.toISOString(),
                    remitTo: "",
                    remitAccount: "",
                    paymentTerm: "",
                    rawData: JSON.parse(JSON.stringify(outstandingFile)) || {},
                })
            }
        }
