function fixDate(invoice) {
    let tempDocDate = new Date(invoice.postingDate);
    let tempSystemDueDate = new Date(invoice.sytemDueDate);
    let tempDueDate = new Date(invoice.dueDate);

    tempDocDate.setTime(tempDocDate.getTime() + 1000 * 60);
    tempDocDate.setHours(0, 0, 0);
    tempSystemDueDate.setTime(tempSystemDueDate.getTime() + 1000 * 60);
    tempSystemDueDate.setHours(0, 0, 0);
    tempDueDate.setTime(tempDueDate.getTime() + 1000 * 60);
    tempDueDate.setHours(0, 0, 0);

    invoice.docDate = convertToISO(tempDocDate);
    invoice.sytemDueDate = convertToISO(tempSystemDueDate);
    invoice.dueDate = convertToISO(tempDueDate);
}

function convertToISO(date) {
    return (
        date.getFullYear() +
        "-" +
        (date.getMonth() + 1) +
        "-" +
        date.getDate() +
        " 00:00:00.000+00"
    );
}

function sliceIntoChunks(arr, chunkSize) {
    const res = [];

    for (let k = 0; k < arr.length; k += chunkSize) {
        const chunk = arr.slice(k, k + chunkSize);
        res.push(chunk);
    }

    return res;
};

function addDays(days, invoiceDate) {
    var date = new Date(invoiceDate);
    date.setDate(date.getDate() + days);
    return date;
}

function formatDates(inputDate) {
    const date = inputDate
    const [day, month, year] = date.toString()?.split("/")
    const result = new Date(year, month - 1, day).toLocaleDateString('en-GB');
    return result.split("/").reverse().join("-")
}

function sheetDateToJSDate(n) {
    // const d = new Date(1899, 11, 30)
    const d = new Date(1899, 11, 30)
    d.setDate(d.getDate() + n)
    // Rounds milliseconds to seconds
    d.setSeconds(d.getSeconds() + Math.round(d.getMilliseconds() / 1000))
    d.setMilliseconds(0)
    // return `${d.getDate()}-${d.getMonth() + 1}-${d.getFullYear()}`
    return `${d.getFullYear()}-${d.getMonth() + 1}-${d.getDate()}`
}

